  INDEX `fk_suscripcion_user_idx` (`usuario_userfav` ASC),
  CONSTRAINT `fk_recetaFavoritas_usuario1`
    FOREIGN KEY (`usuario_userfav`)
    REFERENCES `usuario` (`user`)
    ON DELETE NO ACTION
    ON UPDATE NO ACTION
);



CREATE TABLE IF NOT EXISTS `suscripcion` (
