  `followed_user` VARCHAR(45) NOT NULL,
  `my_user` VARCHAR(45) NOT NULL,
  PRIMARY KEY (`idsuscripcion`),
  INDEX `fk_suscripcion_usuario1_idx` (`my_user` ASC),
  CONSTRAINT `fk_suscripcion_user1`
